    suspend fun `abort`()
    
    suspend fun `connectReady`(`tryToUnlock`: kotlin.Boolean): SessionState
    
    suspend fun `connectReadyWithPolicy`(`tryToUnlock`: kotlin.Boolean, `retryPolicy`: SessionRetryPolicy?): SessionState
    
