    suspend fun `hostConfig`(): HostConfig
    
    suspend fun `nextEvent`(`timeoutMs`: kotlin.ULong?): WorkflowEvent?
    
    suspend fun `pairOnly`(`tryToUnlock`: kotlin.Boolean): SessionState
    
    suspend fun `pairOnlyWithPolicy`(`tryToUnlock`: kotlin.Boolean, `retryPolicy`: SessionRetryPolicy?): SessionState
    
    suspend fun `pairingConfirmConnection`(): PairingProgress
    
    suspend fun `pairingStart`(): PairingPrompt
    
