/**
 * Simplified error surface exposed via UniFFI bindings.
 */
sealed class HwCoreException(message: String): kotlin.Exception(message) {
        
        class Ble(message: String) : HwCoreException(message)
        
        class Workflow(message: String) : HwCoreException(message)
        
        class Device(message: String) : HwCoreException(message)
        
        class Validation(message: String) : HwCoreException(message)
        
        class Timeout(message: String) : HwCoreException(message)
        
        class Unknown(message: String) : HwCoreException(message)
        

    companion object ErrorHandler : UniffiRustCallStatusErrorHandler<HwCoreException> {
        override fun lift(error_buf: RustBuffer.ByValue): HwCoreException = FfiConverterTypeHWCoreError.lift(error_buf)
    }
}

