        return 4UL
    }

    override fun write(value: HwCoreException, buf: ByteBuffer) {
        when(value) {
            is HwCoreException.Ble -> {
                buf.putInt(1)
                Unit
            }
            is HwCoreException.Workflow -> {
                buf.putInt(2)
                Unit
            }
            is HwCoreException.Device -> {
                buf.putInt(3)
                Unit
            }
            is HwCoreException.Validation -> {
                buf.putInt(4)
                Unit
