            is HwCoreException.Timeout -> {
                buf.putInt(5)
                Unit
            }
            is HwCoreException.Unknown -> {
                buf.putInt(6)
                Unit
            }
        }.let { /* this makes the `when` an expression, which ensures it is exhaustive */ }
    }

