/**
 * @suppress
 */
public object FfiConverterTypeSessionHandshakeState : FfiConverterRustBuffer<SessionHandshakeState>{
    override fun read(buf: ByteBuffer): SessionHandshakeState {
        return when(buf.getInt()) {
            1 -> SessionHandshakeState.Ready
            2 -> SessionHandshakeState.PairingRequired(
                FfiConverterTypePairingPrompt.read(buf),
                )
            3 -> SessionHandshakeState.ConnectionConfirmationRequired(
                FfiConverterTypePairingPrompt.read(buf),
                )
            else -> throw RuntimeException("invalid enum value, something is very wrong!!")
        }
    }

    override fun allocationSize(value: SessionHandshakeState) = when(value) {
        is SessionHandshakeState.Ready -> {
            // Add the size for the Int that specifies the variant plus the size needed for all fields
            (
                4UL
            )
        }
        is SessionHandshakeState.PairingRequired -> {
            // Add the size for the Int that specifies the variant plus the size needed for all fields
            (
                4UL
                + FfiConverterTypePairingPrompt.allocationSize(value.`prompt`)
            )
        }
        is SessionHandshakeState.ConnectionConfirmationRequired -> {
            // Add the size for the Int that specifies the variant plus the size needed for all fields
            (
                4UL
                + FfiConverterTypePairingPrompt.allocationSize(value.`prompt`)
            )
        }
    }

    override fun write(value: SessionHandshakeState, buf: ByteBuffer) {
        when(value) {
            is SessionHandshakeState.Ready -> {
                buf.putInt(1)
                Unit
            }
            is SessionHandshakeState.PairingRequired -> {
                buf.putInt(2)
                FfiConverterTypePairingPrompt.write(value.`prompt`, buf)
                Unit
            }
            is SessionHandshakeState.ConnectionConfirmationRequired -> {
                buf.putInt(3)
                FfiConverterTypePairingPrompt.write(value.`prompt`, buf)
                Unit
            }
        }.let { /* this makes the `when` an expression, which ensures it is exhaustive */ }
    }
}






